-- ----------------------------
-- Table structure for book
-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT '书籍id',
  `book_title` varchar(255) NOT NULL COMMENT '书籍标题',
  `book_desc` varchar(255) NOT NULL COMMENT '书籍描述',
  `author_id` int DEFAULT NULL COMMENT '作者id',
  `chapter_total` int DEFAULT NULL COMMENT '总章节数量',
  `copyright` varchar(255) DEFAULT NULL COMMENT '版权方 出版社',
  `state` tinyint DEFAULT '1' COMMENT '状态 默认1 待上架 5 已上架 0 删除',
  `category_id` int DEFAULT '1' COMMENT '分类id',
  PRIMARY KEY (`id`),
  KEY `idx` (`state`),
  KEY `idx_categry` (`category_id`),
  KEY `idx_author` (`author_id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='书籍';

-- ----------------------------
-- Records of book
-- ----------------------------
INSERT INTO `book` VALUES ('1', '家族修仙从养鱼开始', '穿越重生至修仙家族李家，只是灵根有点差，行吧，练符养鱼赚灵石，资质不够，灵石来凑。 好兄弟修行太慢需要指导一下？却发现了了不得的大事。 震惊：四哥竟然是五灵根修士，却修出了三灵根的修练速度。 什么，老六竟然是暗灵根？我之前还怕你吃苦，又怕你受累，才辛苦指导你修炼，你竟然是暗灵根！ 好吧，李祥云第一次酸了。 【凡人流】【家族流】【种田流】', '1', '1290', '潇潇暮雪归   著   连载 · 签约 · VIP · 仙侠 · 修真文明', '5', '1');
INSERT INTO `book` VALUES ('2', '从聊斋开始修改命数', '【杀伐果断】【凡人流】【种田】【聊斋味】 简介一： 美人皮，狐嫁女，醉来舞剑天倾雨。 掌中剑，壶中妖，腰间悬剑仗逍遥。 “三十六州尘烟正嚣，九万里剑气招摇，我徐长生无甚所长，唯有一心，一剑，一酒壶而已。”', '2', '1480', '胡北枳   著   连载 · 签约 · VIP · 仙侠 · 古典仙侠', '5', '1');
INSERT INTO `book` VALUES ('3', '太古第一仙', '云逍死后魂飞天际时震撼发现：他生活了十六年的浩瀚人间，形状竟像一座坟墓！坟下一尊太古铜棺，大如沧溟，魔气滔天，一座座陆地都是棺上一抔抔黄土，万万亿活人竟在坟土中繁衍生息！巨棺内一具仙尸，其眼如地府，口若黄泉，头发似苍龙，身体像百万山脉。不知何方仙神，万古长眠于此！ 少年冤魂不灭，遁入仙尸还魂重生，以世界天柱为剑，以浮生大陆为符，一人一棺杀穿仙路，通天证道！', '3', '890', '作者： 风青阳   授权级别：	签约作品	作品类别：	古典仙侠', '5', '1');
INSERT INTO `book` VALUES ('4', '凌天剑帝', '仙界战神凌天被兄弟天尊偷袭而死，转世重生于凡界，成为安家赘婿，得上古至尊神器周天星辰图，修鸿蒙炼天决，开启了一段横扫九天十地无数天骄的崛起传奇。这一世，他s  如果您喜欢凌天剑帝，别忘记分享给朋友.', '5', '789', '作    者：墨白本尊  最后更新：2023-09-11 00:07:07 ', '5', '3');
INSERT INTO `book` VALUES ('5', '都市无敌战神', '五年前，被陷害入狱！五年后，他荣耀归来，天下权势，尽握手中！我所失去的，终会千百倍的拿回来！作者：17409187作者微博：我不是本尊s  如果您喜欢都市无敌战神，别忘记分享给朋友.', '6', '876', '作者：我不是z所写', '5', '2');
INSERT INTO `book` VALUES ('6', '家父隋炀帝', '世子爷，圣后在永安宫召见，奴婢在外候着，您准备一下。”  寝殿外，独孤后的贴身女婢阿楼的声音传来。', '7', '563', '作者：圆盘大佬粗', '5', '4');
INSERT INTO `book` VALUES ('7', '大明：家父永乐，永镇山河', '建文末年，燕王入京，即皇帝位，年号永乐。一个伟大的帝国，就此掀开了一页崭新篇章，即将迎来千古治世。后世青年不幸魂穿大名鼎鼎的烤肉王爷朱高煦，面对英明神武的s', '8', '679', '作者：朕闻上古', '5', '4');

