INSERT INTO `comment` VALUES ('15', '3', '3', '3', '【test3】:人生百态，各有千秋。一个人生活在这个世界上，就要面对各种各样的人、各种各样的事、各种各样的人际关系。一个人不管有多聪明，多能干，背景条件有多好，如果不懂得如何做人、做事，那么他最终的结局肯定是失败。', '0');

-- ----------------------------
-- Table structure for feedback
-- ----------------------------
DROP TABLE IF EXISTS `feedback`;
CREATE TABLE `feedback` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `member_id` int DEFAULT NULL COMMENT '用户id',
  `feed` varchar(255) DEFAULT NULL COMMENT '反馈内容',
  `feed_day` varchar(255) DEFAULT NULL COMMENT '反馈时间',
  `callback` varchar(255) DEFAULT NULL COMMENT '回复内容',
  `user_id` int DEFAULT NULL COMMENT '回复后台id',
  PRIMARY KEY (`id`),
  KEY `idx` (`member_id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='用户反馈记录表';

-- ----------------------------
-- Records of feedback
-- ----------------------------
INSERT INTO `feedback` VALUES ('1', '1', '希望小说内容能快点更新 ', '2023-09-10 10:00:00', '感谢您的关注，希望能持续给您带来好的体验。', '2');
INSERT INTO `feedback` VALUES ('2', '3', '分身乏术发多少放入工会个人和规范化复工后讽德诵功梵蒂冈讽德诵功讽德诵功讽德诵功讽德诵功', '2023-09-10 16:56:28', '', '0');
INSERT INTO `feedback` VALUES ('3', '3', '法大使馆的萨公司当地方地方地方', '2023-09-10 16:59:34', '', '0');
INSERT INTO `feedback` VALUES ('4', '3', '范德萨范德萨发第三方速度都是', '2023-09-10 16:59:37', '', '0');
INSERT INTO `feedback` VALUES ('5', '3', '【test3】:发生的恢复施工法大使馆梵蒂冈发的是发大V发大V光环讽德诵功讽德诵功发的功法轨顶风道施工方都是丰东股份都是割发代首割发代首讽德诵功发多少股份的时光', '2023-09-11 00:17:40', '', '0');
INSERT INTO `feedback` VALUES ('6', '3', '【test3】:优酷与考核方式讽德诵功讽德诵功发施工方都是割发代首高富帅发多少', '2023-09-11 00:17:48', '', '0');
INSERT INTO `feedback` VALUES ('7', '3', '【test3】:《黄鹤楼送孟浩然之广陵》\n唐·李白\n故人西辞黄鹤楼，烟花三月下扬州。孤帆远影碧空尽，唯见长江天际流。', '2023-09-11 00:18:15', '', '0');
INSERT INTO `feedback` VALUES ('8', '3', '【test3】:《将进酒·君不见》\n唐·李白\n君不见，黄河之水天上来，奔流到海不复回。君不见，高堂明镜悲白发，朝如青丝暮成雪。人生得意须尽欢，莫使金樽空对月。天生我材必有用，千金散尽还复来', '2023-09-11 00:18:36', '', '0');
INSERT INTO `feedback` VALUES ('9', '3', '【test3】:《送元二使安西》\n唐·王维\n渭城朝雨浥轻尘，客舍青青柳色新。劝君更尽一杯酒，西出阳关无故人。', '2023-09-11 00:18:57', '', '0');
INSERT INTO `feedback` VALUES ('10', '3', '【test3】:《咏柳》\n唐·贺知章\n碧玉妆成一树高，万条垂下绿丝绦。不知细叶谁裁出，二月春风似剪刀。', '2023-09-11 00:19:07', '', '0');
INSERT INTO `feedback` VALUES ('11', '3', '【test3】:白日依山尽，黄河入海流。欲穷千里目，更上一层楼。春眠不觉晓，处处闻啼鸟。夜来风雨声，花落知多少。秦时明月汉时关，万里长征人未还。但使龙城飞将在，不教胡马渡阴山。', '2023-09-11 00:19:59', '', '0');

