-- ----------------------------
-- Table structure for member_reader
-- ----------------------------
DROP TABLE IF EXISTS `member_reader`;
CREATE TABLE `member_reader` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `member_id` int unsigned DEFAULT NULL COMMENT '用户id',
  `book_id` int unsigned DEFAULT NULL COMMENT '书籍id',
  `chapter_id` int DEFAULT NULL COMMENT '章节id',
  `last_update` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '最后一次更新',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx-m` (`member_id`,`book_id`,`chapter_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='用户阅读记录';

